sources.style.css:
* {
  margin: 0;
  padding: 0;
}

body {
  font-family: "Noto Kufi Arabic", sans-serif;
}

.header {
  height: 310px;
  width: 100%;

  background-image: url("./images/header-img-mob.png");
  background-repeat: no-repeat;
  background-size: cover;
}

@media (min-width: 640px) {
  .header {
    height: 415px;
  }
}

@media (min-width: 768px) {
  .header {
    height: 500px;
  }
}

/*Тут я зробив 1400 чисто для фону,
бо на мою думку не у всіх юзерів екрани 1920
, а якщо зробити 1920 то наприклад на моєму екрані фон , буде розтягнутий) */
@media (min-width: 1400px) {
  .header {
    background-image: url("./images/header-img-desktop.png");
  }
}

@media (min-width: 1920px) {
  .header {
    height: 467.5px;
  }
}

.header__container {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-right: 95px;
  padding-left: 95px;
}

@media (min-width: 640px) {
  .header__container {
    padding-left: 144px;
    padding-right: 144px;
  }
}

@media (min-width: 768px) {
  .header__container {
    padding-right: 174px;
    padding-left: 174px;
  }
}

@media (min-width: 1920px) {
  .header__container {
    padding-right: 473.74px;
    padding-left: 955.26px;
    align-items: flex-end;
  }
}

.header__title {
  margin-bottom: 7px;
  margin-top: 17px;

  font-size: 21px;
  font-weight: bold;
  text-align: center;
  letter-spacing: 0;

  color: #010308;
}

@media (min-width: 640px) {
  .header__title {
    margin-top: 21px;
    font-size: 28px;
  }
}

@media (min-width: 768px) {
  .header__title {
    margin-top: 26px;
    font-size: 33.59px;
  }
}

@media (min-width: 1920px) {
  .header__title {
    font-size: 46.34px;
    margin-top: 122px;
  }
}

.header__description {
  margin-bottom: 12px;

  font-size: 14.8px;
  text-align: center;
  line-height: 25px;

  color: #010308;
}

@media (min-width: 640px) {
  .header__description {
    font-size: 18px;
    line-height: 30px;
  }
}

@media (min-width: 768px) {
  .header__description {
    margin-bottom: 20px;

    font-size: 21px;
    line-height: 36px;
  }
}

@media (min-width: 1920px) {
  .header__description {
    text-align: right;
    font-size: 21.03px;
    line-height: 34px;
  }
}

.header__button {
  width: 200px;
  height: 30px;
  margin-bottom: 10px;

  font-family: "Noto Kufi Arabic", sans-serif;
  font-size: 15.92px;
  font-weight: bold;
  text-align: center;

  color: #ffff;
  background-color: #dd0b0c;
  border: none;

  transition: 0.5s;

  cursor: pointer;
}

.header__button:hover {
  background-color: #990909;
  transition: 0.5s;
}

@media (min-width: 640px) {
  .header__button {
    width: 266px;
    height: 34px;
    font-size: 17px;
    margin-bottom: 26px;
  }
}

@media (min-width: 768px) {
  .header__button {
    width: 320px;
    height: 42px;
    margin-bottom: 20px;
    font-size: 21.49px;
  }
}

@media (min-width: 1920px) {
  .header__button {
    width: 315px;
    height: 40px;
    margin-bottom: 0;
  }
}

@media (min-width: 640px) {
  .header__books {
    width: 236px;
    height: 233px;
  }
}

@media (min-width: 768px) {
  .header__books {
    width: 282px;
    height: 278px;
  }
}

@media (min-width: 1920px) {
  .header__books {
    position: absolute;
    top: 7px;
    left: 473px;
    width: 495px;
    height: 488px;
  }
}

.main {
  background-image: url(./images/main-background.png);
  background-repeat: no-repeat;
  background-size: cover;
  height: 100%;
}

.main__container {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-right: 125px;
  padding-left: 125px;
}

@media (min-width: 640px) {
  .main__container {
    padding-left: 188px;
    padding-right: 188px;
  }
}

@media (min-width: 768px) {
  .main__container {
    padding-left: 225px;
    padding-right: 225px;
  }
}

@media (min-width: 1920px) {
  .main__container {
    padding-left: 701px;
    padding-right: 701px;
  }
}

.main__title {
  font-size: 21px;
  font-weight: bold;
  padding-top: 34.66px;
  text-align: center;
  color: #fff;
  margin-bottom: 14.71px;
}

@media (min-width: 640px) {
  .main__title {
    padding-top: 42.5px;
    line-height: 39px;
    font-size: 22px;
    margin-bottom: 17px;
  }
}

@media (min-width: 768px) {
  .main__title {
    padding-top: 48px;
    font-size: 26.4px;
    margin-bottom: 21px;
    line-height: 34px;
  }
}

@media (min-width: 1920px) {
  .main__title {
    padding-top: 42px;
    font-size: 26px;
    margin-bottom: 19px;
  }
}

.main__button {
  width: 200px;
  height: 30px;

  text-align: center;
  color: #000;

  background-color: #f3b411;
  border: none;

  font-family: "Noto Kufi Arabic", sans-serif;
  font-size: 15.9px;
  font-weight: bold;

  margin-bottom: 13px;
  cursor: pointer;
  transition: 0.5s;
}

.main__button:hover {
  background-color: #c28e0d;
  transition: 0.5s;
}

@media (min-width: 640px) {
  .main__button {
    width: 266px;
    height: 34px;
    font-size: 17.87px;
    margin-bottom: 16px;
  }
}

@media (min-width: 768px) {
  .main__button {
    width: 320px;
    height: 41px;
    font-size: 21.45px;
    margin-bottom: 19px;
  }
}

@media (min-width: 1920px) {
  .main__button {
    width: 223px;
    height: 41px;
    font-size: 19.41px;
    margin-bottom: 19px;
  }
}

.main__subtitle {
  font-size: 15.65px;
  font-weight: bold;
  color: #fff;
  margin-bottom: 18px;
}

@media (min-width: 640px) {
  .main__subtitle {
    font-size: 18px;
  }
}

@media (min-width: 768px) {
  .main__subtitle {
    font-size: 21.6px;
  }
}

@media (min-width: 1920px) {
  .main__subtitle {
    font-size: 21.99px;
  }
}

.main__list {
  list-style-type: none;
  margin-bottom: 34px;
}

@media (min-width: 1920px) {
  .main__list {
    margin-bottom: 30px;
  }
}

.main__list-item {
  font-family: "Noto Naskh Arabic", serif;
  text-align: right;
  color: white;
  line-height: 27px;
}

@media (min-width: 640px) {
  .main__list-item {
    font-size: 16px;
    line-height: 31px;
  }
}

@media (min-width: 768px) {
  .main__list-item {
    font-size: 19.2px;
    line-height: 37px;
  }
}

@media (min-width: 1920px) {
  .main__list-item {
    font-size: 19.98px;
    line-height: 34px;
  }
}

.footer {
  /* position: absolute;
  left: 0;
  bottom: 0; */

  height: 130px;
  width: 100%;

  background-color: #3e3e3e;
}

@media (min-width: 640px) {
  .footer {
    height: 125px;
  }
}

@media (min-width: 768px) {
  .footer {
    height: 360px;
  }
}

@media (min-width: 1920px) {
  .footer {
    height: 166px;
  }
}
